import axios from "axios";

export default {
  props: {
    value: [String, Number],
    api: String,
    all: {
      type: Boolean,
      default: false
    },
    label: {
      type: String,
      default: ""
    },
    placeholder: {
      type: String,
      default: ""
    },
    disabled: {
      type: Boolean,
      default: false
    },
    error: {
      type: String,
      default: ""
    }
  },
  data: function() {
    return {
      options: []
    };
  },
  created() {
    this.fitchData();
  },
  methods: {
    fitchData() {
      axios
        .get(this.api)
        .then(response => {
          if (response.data.status == "ok") {
            console.log(this.value);

            this.options = response.data.data;
          }
        })
        .catch(e => {
          console.log(e);
        });
    }
  }
};
